import { createSelector } from "@ngrx/store";
import { StateEnum } from "../models/enums.model";
export const selectRoot = state => state.krs;
export const selectKRList = createSelector(selectRoot, state => state.list);
export const selectSortByOptions = createSelector(selectRoot, state => state.sortByOptions);
export const selectKRLoading = createSelector(selectRoot, state => state.loading);
export const selectActiveStates = createSelector(selectRoot, state => state.activeStates);
export const selectSortBySelectedOption = createSelector(selectRoot, state => state.sortBySelectedOption);
export const selectFilteredKRList = createSelector(selectKRList, selectActiveStates, (list, activeStates) => {
  if (activeStates.length == 1 && activeStates[0] === StateEnum.ALL) return list;
  return list.filter(kr => activeStates.some(state => state === kr.state));
});
export const selectSortedKRList = createSelector(selectFilteredKRList, selectSortBySelectedOption, (list, sortBy) => {
  console.log(list);
  return list.slice().sort((a, b) => {
    switch (sortBy) {
      case 1:
        return b.krCardNumber.localeCompare(a.krCardNumber);

      case 2:
        return a.krCardNumber.localeCompare(b.krCardNumber);
        ;

      case 3:
        return b.date.localeCompare(a.date);

      default:
        return a.date.localeCompare(b.date);
    }
  });
});
export const selectedKR = createSelector(selectRoot, state => state.activeKR);
export const selectedKRId = createSelector(selectedKR, KrCard => KrCard.card.id);
